import React, { useState } from 'react';

const Recipes = ({ updateInventory }) => {
  const [recipes, setRecipes] = useState([]);
  const [newRecipe, setNewRecipe] = useState({ name: '', ingredients: [] });
  const [ingredient, setIngredient] = useState({ name: '', quantity: 0 });

  const addRecipe = () => {
    if (newRecipe.name.trim() && newRecipe.ingredients.length > 0) {
      setRecipes([...recipes, newRecipe]);
      setNewRecipe({ name: '', ingredients: [] });
    }
  };

  const addIngredient = () => {
    if (ingredient.name && ingredient.quantity > 0) {
      setNewRecipe({
        ...newRecipe,
        ingredients: [...newRecipe.ingredients, ingredient],
      });
      setIngredient({ name: '', quantity: 0 });
    }
  };

  const handleCookRecipe = (recipe) => {
    const confirmCook = window.confirm(`Are you going to cook ${recipe.name}?`);
    if (confirmCook) {
      recipe.ingredients.forEach((ingredient) => {
        updateInventory(ingredient.name, ingredient.quantity);
      });
      alert(`You have cooked ${recipe.name}! Inventory updated.`);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Recipes</h1>
      <div>
        <input
          type="text"
          value={newRecipe.name}
          onChange={(e) => setNewRecipe({ ...newRecipe, name: e.target.value })}
          placeholder="Recipe Name"
        />
        <input
          type="text"
          value={ingredient.name}
          onChange={(e) => setIngredient({ ...ingredient, name: e.target.value })}
          placeholder="Ingredient"
        />
        <input
          type="number"
          value={ingredient.quantity}
          onChange={(e) => setIngredient({ ...ingredient, quantity: parseInt(e.target.value) })}
          placeholder="Quantity Needed"
        />
        <button onClick={addIngredient}>Add Ingredient</button>
        <button onClick={addRecipe}>Add Recipe</button>
      </div>

      <ul>
        {recipes.map((recipe, index) => (
          <li key={index}>
            {recipe.name} - Ingredients:
            <ul>
              {recipe.ingredients.map((ingredient, idx) => (
                <li key={idx}>
                  {ingredient.name}: {ingredient.quantity}
                </li>
              ))}
            </ul>
            <button onClick={() => handleCookRecipe(recipe)}>Cook this Recipe</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Recipes;
