import React, { useState, useEffect, useContext } from 'react';
import { db } from '../services/firebase';
import { doc, setDoc } from 'firebase/firestore';
import { AppContext } from '../context/AppContext';

const ShoppingList = () => {
  const { inventory, recipes, shoppingList, setShoppingList } = useContext(AppContext);
  const [itemName, setItemName] = useState('');
  const [quantity, setQuantity] = useState('');

  // Load shopping list from localStorage when the component mounts
  useEffect(() => {
    const storedItems = localStorage.getItem('shoppingList');
    if (storedItems) {
      setShoppingList(JSON.parse(storedItems));
    }

    const intervalId = setInterval(() => {
      saveToFirebase(shoppingList);
    }, 300000);  // 5 minutes interval

    return () => clearInterval(intervalId);
  }, []);

  // Sync shopping list with localStorage and Firebase
  useEffect(() => {
    localStorage.setItem('shoppingList', JSON.stringify(shoppingList));
    saveToFirebase(shoppingList);
  }, [shoppingList]);

  // Sync with inventory: remove items from shopping list if restocked
  useEffect(() => {
    setShoppingList(prevList => prevList.filter(item => !inventory.some(inv => inv.name === item.name)));
  }, [inventory]);

  const addItem = () => {
    if (itemName && quantity) {
      setShoppingList(prevItems => [...prevItems, { name: itemName, quantity }]);
      setItemName('');
      setQuantity('');
    }
  };

  const removeItem = (index) => {
    setShoppingList(prevItems => prevItems.filter((_, i) => i !== index));
  };

  const clearList = () => {
    setShoppingList([]);
  };

  // Add missing ingredients from a selected recipe to the shopping list
  const addRecipeToShoppingList = (recipeId) => {
    const selectedRecipe = recipes.find(r => r.id === recipeId);
    if (selectedRecipe) {
      const missingItems = selectedRecipe.ingredients.filter(ing => !inventory.some(inv => inv.name === ing.name));
      setShoppingList(prevList => [...prevList, ...missingItems]);
    }
  };

  const saveToFirebase = async (data) => {
    try {
      await setDoc(doc(db, 'shoppingList', 'user-shopping-list'), {
        items: data,
        lastUpdated: new Date(),
      });
      console.log('Shopping list saved to Firebase.');
    } catch (error) {
      console.error('Error saving shopping list to Firebase:', error);
    }
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h1>Shopping List</h1>

      <div style={styles.inputGroup}>
        <label style={styles.label}>Item Name: </label>
        <input
          type="text"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          placeholder="Enter item name"
          style={styles.inputName}
        />
      </div>

      <div style={styles.inputGroup}>
        <label style={styles.label}>Quantity: </label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          style={styles.inputSmall}
        />
      </div>

      <button onClick={addItem} style={styles.addButton}>Add Item</button>
      <button onClick={clearList} style={styles.clearButton}>Clear List</button>

      <div style={styles.listContainer}>
        {shoppingList.length > 0 ? (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {shoppingList.map((item, index) => (
              <li key={index} style={styles.listItem}>
                <span style={styles.itemName}>{item.name}</span>
                <span style={styles.itemQuantity}>x{item.quantity}</span>
                <button onClick={() => removeItem(index)} style={styles.removeButton}>Remove</button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No items in the shopping list.</p>
        )}
      </div>

      {/* Recipe Ingredient Sync */}
      <h2>Add Ingredients from Recipe</h2>
      <select onChange={(e) => addRecipeToShoppingList(e.target.value)}>
        <option value="">Select Recipe</option>
        {recipes.map(recipe => (
          <option key={recipe.id} value={recipe.id}>{recipe.name}</option>
        ))}
      </select>
    </div>
  );
};

export default ShoppingList;
