// Inventory Component
import React, { useState } from 'react';

const Inventory = ({ updateShoppingList }) => {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState({
    name: '',
    quantity: 0,
    unit: 'grams',
    price: 0,
    servings: 0,
  });

  const addItem = () => {
    if (newItem.name.trim() && newItem.quantity > 0 && newItem.servings > 0) {
      setItems([...items, newItem]);
      setNewItem({ name: '', quantity: 0, unit: 'grams', price: 0, servings: 0 });
    } else {
      alert('Please enter valid details.');
    }
  };

  const handleUseItem = (itemName, usedQuantity) => {
    const updatedItems = items.map(item => {
      if (item.name === itemName) {
        const newQuantity = item.quantity - usedQuantity;
        if (newQuantity <= item.quantity * 0.25) {
          updateShoppingList(item.name);
        }
        return { ...item, quantity: newQuantity };
      }
      return item;
    });
    setItems(updatedItems);
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h1>Inventory Management</h1>

      <div style={styles.inputGroup}>
        <label style={styles.label}>Item Name:</label>
        <input
          type="text"
          value={newItem.name}
          onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
          placeholder="Enter the name of the item"
          style={styles.inputName}
        />
      </div>

      <div style={styles.inputGroup}>
        <label style={styles.label}>Quantity:</label>
        <input
          type="number"
          value={newItem.quantity}
          onChange={(e) => setNewItem({ ...newItem, quantity: parseInt(e.target.value) })}
          placeholder="Enter the total quantity"
          style={styles.inputSmall}
        />
      </div>

      <div style={styles.inputGroup}>
        <label style={styles.label}>Number of Servings:</label>
        <input
          type="number"
          value={newItem.servings}
          onChange={(e) => setNewItem({ ...newItem, servings: parseInt(e.target.value) })}
          placeholder="Enter number of servings"
          style={styles.inputSmall}
        />
      </div>

      <div style={styles.inputGroup}>
        <label style={styles.label}>Price per Unit:</label>
        <input
          type="number"
          value={newItem.price}
          onChange={(e) => setNewItem({ ...newItem, price: parseFloat(e.target.value) })}
          placeholder="Enter price per unit"
          style={styles.inputSmall}
        />
      </div>

      <div style={styles.inputGroup}>
        <label style={styles.label}>Unit:</label>
        <select
          value={newItem.unit}
          onChange={(e) => setNewItem({ ...newItem, unit: e.target.value })}
          style={styles.inputSelect}
        >
          <option value="grams">grams</option>
          <option value="ml">mL</option>
          <option value="tsp">tsp</option>
          <option value="tbsp">tbsp</option>
        </select>
      </div>

      <button onClick={addItem}>Add Item</button>

      <ul style={{ marginTop: '20px' }}>
        {items.map((item, index) => (
          <li key={index}>
            {item.name} - {item.quantity} {item.unit} remaining
            <button onClick={() => handleUseItem(item.name, item.servings)}>Use in Recipe</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

// Styles for Inventory Component
const styles = {
  inputGroup: {
    marginBottom: '10px',  // Adds space between label and input field
  },
  label: {
    fontFamily: "'Dancing Script', cursive",
    fontSize: "18px",
    color: "#3e2723",
    marginBottom: '5px',
  },
  inputName: {
    width: '97%',  // Item name width
    padding: '10px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontFamily: "'Dancing Script', cursive",
    fontSize: '16px',
  },
  inputSmall: {
    width: '80px',  // Smaller boxes
    padding: '8px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontFamily: "'Dancing Script', cursive",
    fontSize: '16px',
  },
  inputSelect: {
    padding: '8px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontFamily: "'Dancing Script', cursive",
    fontSize: '16px',
  },
};

export default Inventory;
