import React, { useState, useEffect } from 'react';
import { fetchRecipe } from '../services/ai'; // Adjust the path according to your structure

const Recipes = ({ updateInventory, inventory, addToShoppingList }) => {
  const [recipes, setRecipes] = useState([]);
  const [newRecipe, setNewRecipe] = useState({
    name: '',
    ingredients: Array(1).fill({ name: '', quantity: 0, unit: 'grams' }),
    procedure: [''],
  });
  const [expandedRecipe, setExpandedRecipe] = useState(null);
  const [aiIngredients, setAIIngredients] = useState('');
  const [aiRecipe, setAIRecipe] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const addIngredientField = () => {
    setNewRecipe({
      ...newRecipe,
      ingredients: [...newRecipe.ingredients, { name: '', quantity: 0, unit: 'grams' }],
    });
  };

  const handleIngredientChange = (index, field, value) => {
    const updatedIngredients = [...newRecipe.ingredients];
    updatedIngredients[index] = {
      ...updatedIngredients[index],
      [field]: value,
    };
    setNewRecipe({ ...newRecipe, ingredients: updatedIngredients });
  };

  const handleProcedureChange = (index, value) => {
    const updatedProcedure = [...newRecipe.procedure];
    updatedProcedure[index] = value;
    setNewRecipe({ ...newRecipe, procedure: updatedProcedure });
  };

  const addProcedureStep = () => {
    setNewRecipe({ ...newRecipe, procedure: [...newRecipe.procedure, ''] });
  };

  const saveRecipe = () => {
    if (newRecipe.name.trim() && newRecipe.ingredients.some((ing) => ing.name.trim())) {
      setRecipes([...recipes, newRecipe]);
      setNewRecipe({
        name: '',
        ingredients: Array(1).fill({ name: '', quantity: 0, unit: 'grams' }),
        procedure: [''],
      });
    } else {
      alert('Please enter a recipe name and at least one ingredient.');
    }
  };

  const handleCookRecipe = (recipe) => {
    const missingIngredients = recipe.ingredients.filter(
      (ingredient) => 
        !inventory[ingredient.name] || inventory[ingredient.name] < ingredient.quantity
    );

    if (missingIngredients.length === 0) {
      const confirmCook = window.confirm(`Are you going to cook ${recipe.name}?`);
      if (confirmCook) {
        recipe.ingredients.forEach((ingredient) => {
          if (ingredient.name.trim() && ingredient.quantity > 0) {
            updateInventory(ingredient.name, ingredient.quantity);
          }
        });
        alert(`You have cooked ${recipe.name}! Inventory updated.`);
      }
    } else {
      const missingNames = missingIngredients.map(ingredient => ingredient.name).join(", ");
      alert(`You do not have the following ingredients: ${missingNames}. They have been added to your shopping list.`);
      missingIngredients.forEach(ingredient => addToShoppingList(ingredient.name, ingredient.quantity));
    }
  };

  const toggleRecipeExpansion = (index) => {
    setExpandedRecipe(expandedRecipe === index ? null : index);
  };

  const parseRecipe = (recipeText) => {
    const recipeParts = recipeText.split('Ingredients:');
    const titlePart = recipeParts[0].trim();
    const ingredientsPart = recipeParts[1].split('Instructions:')[0].trim();
    const instructionsPart = recipeParts[1].split('Instructions:')[1].trim();

    return {
      name: titlePart.replace('Recipe:', '').trim(),
      ingredients: ingredientsPart.split('\n').map(ingredient => ({
        name: ingredient.trim(),
        quantity: 1, // Default quantity, can be adjusted
        unit: 'units', // Default unit, can be adjusted
      })),
      procedure: instructionsPart.split('\n').map(instruction => instruction.trim()),
    };
  };

  const handleGetAIRecipe = async () => {
    if (!aiIngredients.trim()) {
      alert('Please enter some ingredients.');
      return;
    }

    setIsLoading(true);
    setAIRecipe(null);

    try {
      const fetchedRecipe = await fetchRecipe(aiIngredients);
      const structuredRecipe = parseRecipe(fetchedRecipe);
      setAIRecipe(structuredRecipe);
    } catch (error) {
      alert('Failed to fetch recipe. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSaveAIRecipe = () => {
    if (aiRecipe) {
      setRecipes([...recipes, aiRecipe]);
      setAIRecipe(null);
      setAIIngredients('');
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Recipe Maker</h1>

      {/* AI Recipe Section */}
      <div style={styles.section}>
        <h3 style={styles.sectionTitle}>AI Recipe Generator</h3>
        <input
          type="text"
          value={aiIngredients}
          onChange={(e) => setAIIngredients(e.target.value)}
          placeholder="Enter ingredients (e.g., tomatoes, pasta)"
          style={styles.input}
        />
        <button onClick={handleGetAIRecipe} disabled={isLoading} style={styles.getRecipeButton}>
          {isLoading ? 'Generating...' : 'Get AI Recipe'}
        </button>

        {aiRecipe && (
          <div style={styles.recipeContainer}>
            <h4>{aiRecipe.name}</h4>
            <div>
              <h5>Ingredients:</h5>
              <ul>
                {aiRecipe.ingredients.map((ingredient, index) => (
                  <li key={index}>{ingredient.name}</li>
                ))}
              </ul>
            </div>
            <div>
              <h5>Procedure:</h5>
              <ul>
                {aiRecipe.procedure.map((step, index) => (
                  <li key={index}>{step}</li>
                ))}
              </ul>
            </div>
            <button onClick={handleSaveAIRecipe} style={styles.saveButton}>
              Save AI Recipe
            </button>
          </div>
        )}
      </div>

      {/* Manual Recipe Section */}
      <div style={styles.inputGroup}>
        <label style={styles.label}>Recipe Name:</label>
        <input
          type="text"
          value={newRecipe.name}
          onChange={(e) => setNewRecipe({ ...newRecipe, name: e.target.value })}
          placeholder="Enter recipe name"
          style={styles.input}
        />
      </div>

      <div style={styles.section}>
        <h3 style={styles.sectionTitle}>Ingredients:</h3>
        {newRecipe.ingredients.map((ingredient, index) => (
          <div key={index} style={styles.ingredientGroup}>
            <input
              type="text"
              value={ingredient.name}
              onChange={(e) => handleIngredientChange(index, 'name', e.target.value)}
              placeholder="Ingredient name"
              style={styles.input}
            />
            <div style={styles.quantityGroup}>
              <label style={styles.label}>Quantity:</label>
              <input
                type="number"
                value={ingredient.quantity}
                onChange={(e) => handleIngredientChange(index, 'quantity', parseInt(e.target.value))}
                placeholder="Quantity"
                style={styles.quantityInput}
              />
              <select
                value={ingredient.unit}
                onChange={(e) => handleIngredientChange(index, 'unit', e.target.value)}
                style={styles.unitSelect}
              >
                <option value="grams">grams</option>
                <option value="mL">mL</option>
                <option value="tsp">tsp</option>
                <option value="tbsp">tbsp</option>
                <option value="cups">cups</option>
                <option value="pieces">pieces</option>
              </select>
            </div>
          </div>
        ))}
        <button style={styles.addButton} onClick={addIngredientField}>
          Add More Ingredients
        </button>
      </div>

      <div style={styles.section}>
        <h3 style={styles.sectionTitle}>Procedure:</h3>
        {newRecipe.procedure.map((step, index) => (
          <div key={index} style={styles.procedureGroup}>
            <textarea
              value={step}
              onChange={(e) => handleProcedureChange(index, e.target.value)}
              placeholder={`Step ${index + 1}`}
              style={styles.textarea}
            />
          </div>
        ))}
        <button style={styles.addButton} onClick={addProcedureStep}>
          Add Another Step
        </button>
      </div>

      <button style={styles.saveButton} onClick={saveRecipe}>
        Save Recipe
      </button>

      <div style={styles.catalog}>
        <h2 style={styles.catalogTitle}>Recipe Catalog</h2>
        <ul style={styles.recipeList}>
          {recipes.map((recipe, index) => (
            <li key={index} style={styles.recipeItem}>
              <div
                onClick={() => toggleRecipeExpansion(index)}
                style={styles.recipeTitle}
              >
                {recipe.name}
              </div>
              {expandedRecipe === index && (
                <div style={styles.recipeDetails}>
                  <h4 style={styles.detailsTitle}>Ingredients:</h4>
                  <ul>
                    {recipe.ingredients.map(
                      (ingredient, idx) =>
                        ingredient.name.trim() && (
                          <li key={idx} style={styles.detailItem}>
                            {ingredient.name}: {ingredient.quantity} {ingredient.unit}
                          </li>
                        )
                    )}
                  </ul>
                  <h4 style={styles.detailsTitle}>Procedure:</h4>
                  <ul>
                    {recipe.procedure.map(
                      (step, idx) =>
                        step.trim() && <li key={idx} style={styles.detailItem}>{step}</li>
                    )}
                  </ul>
                  <button style={styles.cookButton} onClick={() => handleCookRecipe(recipe)}>
                    Cook this Recipe
                  </button>
                </div>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

