import axios from 'axios';

const HF_API_URL = 'https://api.aimlapi.com';
const HF_API_TOKEN = '0f5d83585a2f43ca8590acf3ff3b3802'; // Replace with your token

// Function to fetch recipe suggestions
export const fetchRecipe = async (ingredients) => {
  try {
    const model = 'deepseek-ai/deepseek-llm-67b-chat'; // Recipe model

    const response = await axios.post(
      `${HF_API_URL}/${model}`,
      {
        inputs: `Generate a recipe using the following ingredients: ${ingredients}`,
      },
      {
        headers: {
          Authorization: `Bearer ${HF_API_TOKEN}`,
        },
      }
    );

    const recipe = response.data[0]?.generated_text || 'No recipe found.';
    return recipe;
  } catch (error) {
    console.error('Error fetching recipe:', error);
    throw new Error('Failed to fetch recipe. Please try again.');
  }
};

export default fetchRecipe;
