import React, { useState, useEffect } from 'react';
import { setDoc, doc } from "firebase/firestore";
import { db } from "../services/firebase";

const Recipes = ({ updateInventory, inventory, addToShoppingList }) => {
  const [recipes, setRecipes] = useState([]);
  const [newRecipe, setNewRecipe] = useState({
    name: '',
    ingredients: [{ name: '', quantity: 0, unit: 'grams' }],
    procedure: [''],
  });
  const [expandedRecipe, setExpandedRecipe] = useState(null);
  const [items, setItems] = useState([]); // Inventory items

  // Add ingredient field
  const addIngredientField = () => {
    setNewRecipe({
      ...newRecipe,
      ingredients: [...newRecipe.ingredients, { name: '', quantity: 0, unit: 'grams' }],
    });
  };

  // Handle ingredient input changes
  const handleIngredientChange = (index, field, value) => {
    const updatedIngredients = [...newRecipe.ingredients];
    updatedIngredients[index] = { ...updatedIngredients[index], [field]: value };
    setNewRecipe({ ...newRecipe, ingredients: updatedIngredients });
  };

  // Handle procedure step changes
  const handleProcedureChange = (index, value) => {
    const updatedProcedure = [...newRecipe.procedure];
    updatedProcedure[index] = value;
    setNewRecipe({ ...newRecipe, procedure: updatedProcedure });
  };

  // Add a procedure step
  const addProcedureStep = () => {
    setNewRecipe({ ...newRecipe, procedure: [...newRecipe.procedure, ''] });
  };

  // Save recipe to local state and Firebase
  const saveRecipe = () => {
    if (newRecipe.name.trim() && newRecipe.ingredients.some(ing => ing.name.trim())) {
      setRecipes([...recipes, newRecipe]);
      saveToFirebase([...recipes, newRecipe]); // Save to Firebase

      // Reset newRecipe after saving
      setNewRecipe({
        name: '',
        ingredients: [{ name: '', quantity: 0, unit: 'grams' }],
        procedure: [''],
      });
    } else {
      alert('Please enter a recipe name and at least one ingredient.');
    }
  };

  // Save recipes to Firebase
  const saveToFirebase = async (data) => {
    try {
      await setDoc(doc(db, "Recipes", "user-recipes"), {
        recipes: data,
        lastUpdated: new Date(),
      });
      console.log("Recipe saved to Firebase.");
    } catch (error) {
      console.error("Error saving recipes to Firebase: ", error);
    }
  };

  // Load recipes from Firebase (if needed)
  useEffect(() => {
    const loadRecipesFromFirebase = async () => {
      // Fetch recipes from Firebase if desired, not shown here
    };
    loadRecipesFromFirebase();
  }, []);

  // Cooking a recipe and updating inventory
  const handleCookRecipe = (recipe) => {
    const missingIngredients = recipe.ingredients.filter(ingredient =>
      !inventory[ingredient.name] || inventory[ingredient.name] < ingredient.quantity
    );

    if (missingIngredients.length === 0) {
      const confirmCook = window.confirm(`Are you going to cook ${recipe.name}?`);
      if (confirmCook) {
        recipe.ingredients.forEach(ingredient => {
          if (ingredient.name.trim() && ingredient.quantity > 0) {
            updateInventory(ingredient.name, -ingredient.quantity); // Decrease inventory
          }
        });
        alert(`You have cooked ${recipe.name}! Inventory updated.`);
      }
    } else {
      const missingNames = missingIngredients.map(ingredient => ingredient.name).join(", ");
      alert(`You do not have the following ingredients: ${missingNames}. They have been added to your shopping list.`);
      missingIngredients.forEach(ingredient => addToShoppingList(ingredient.name, ingredient.quantity));
    }
  };

  // Toggle recipe details view
  const toggleRecipeExpansion = (index) => {
    setExpandedRecipe(expandedRecipe === index ? null : index);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Recipe Maker</h1>
      {/* Recipe Input Sections */}
      {/* ... (same input code for name, ingredients, procedure) */}

      {/* Save Recipe Button */}
      <button style={styles.saveButton} onClick={saveRecipe}>
        Save Recipe
      </button>

      {/* Recipe Catalog */}
      <div style={styles.catalog}>
        <h2 style={styles.catalogTitle}>Recipe Catalog</h2>
        <ul style={styles.recipeList}>
          {recipes.map((recipe, index) => (
            <li key={index} style={styles.recipeItem}>
              <div onClick={() => toggleRecipeExpansion(index)} style={styles.recipeTitle}>
                {recipe.name}
              </div>
              {expandedRecipe === index && (
                <div style={styles.recipeDetails}>
                  <h4 style={styles.detailsTitle}>Ingredients:</h4>
                  <ul>
                    {recipe.ingredients.map((ingredient, idx) => (
                      <li key={idx} style={styles.detailItem}>
                        {ingredient.name}: {ingredient.quantity} {ingredient.unit}
                      </li>
                    ))}
                  </ul>
                  <h4 style={styles.detailsTitle}>Procedure:</h4>
                  <ul>
                    {recipe.procedure.map((step, idx) => (
                      <li key={idx} style={styles.detailItem}>{step}</li>
                    ))}
                  </ul>
                  <button style={styles.cookButton} onClick={() => handleCookRecipe(recipe)}>
                    Cook this Recipe
                  </button>
                </div>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const styles = {
  container: {
    background: "linear-gradient(to right, #f8f1e7, #e5d9c6)",
    padding: "20px",
    borderRadius: "12px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    maxWidth: "800px",
    margin: "20px auto",
  },
  title: {
    fontFamily: "'Dancing Script', cursive",
    fontSize: "36px",
    fontWeight: "600",
    color: "#3e2723",
    textAlign: "center",
    marginBottom: "20px",
  },
  inputGroup: {
    marginBottom: "20px",
  },
  label: {
    fontFamily: "'Dancing Script', cursive",
    fontSize: "18px",
    color: "#3e2723",
    marginBottom: "5px",
  },
  input: {
    width: "97%",
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontFamily: "'Dancing Script', cursive",
    fontSize: "16px",
  },
  section: {
    marginBottom: "20px",
  },
  sectionTitle: {
    fontFamily: "'Dancing Script', cursive",
    fontSize: "24px",
    color: "#3e2723",
    marginBottom: "10px",
  },
  ingredientGroup: {
    marginBottom: "10px",
  },
  quantityGroup: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    marginTop: "5px",
  },
  quantityInput: {
    width: "80px",
    padding: "8px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontFamily: "'Dancing Script', cursive",
    fontSize: "16px",
  },
  unitSelect: {
    padding: "8px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontFamily: "'Dancing Script', cursive",
    fontSize: "16px",
  },
  procedureGroup: {
    marginBottom: "10px",
  },
  textarea: {
    width: "97%",
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontFamily: "'Dancing Script', cursive",
    fontSize: "16px",
    height: "80px",
  },
  addButton: {
    background: "#3e2723",
    color: "white",
    padding: "8px 16px",
    borderRadius: "8px",
    border: "none",
    fontFamily: "'Dancing Script', cursive",
    fontSize: "16px",
    cursor: "pointer",
    transition: "background 0.3s ease-in-out",
  },
  saveButton: {
    background: "#3e2723",
    color: "white",
    padding: "10px 20px",
    borderRadius: "8px",
    border: "none",
    fontFamily: "'Dancing Script', cursive",
    fontSize: "18px",
    cursor: "pointer",
    transition: "background 0.3s ease-in-out",
    width: "100%",
    marginTop: "20px",
  },
  catalog: {
    marginTop: "40px",
  },
  catalogTitle: {
    fontFamily: "'Dancing Script', cursive",
    fontSize: "28px",
    color: "#3e2723",
    textAlign: "center",
    marginBottom: "20px",
  },
  recipeList: {
    listStyle: "none",
    padding: 0,
  },
  recipeItem: {
    background: "white",
    padding: "15px",
    borderRadius: "8px",
    marginBottom: "10px",
    boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
  },
  recipeTitle: {
    fontFamily: "'Dancing Script', cursive",
    fontSize: "22px",
    color: "#3e2723",
    cursor: "pointer",
  },
  recipeDetails: {
    marginTop: "10px",
  },
  detailsTitle: {
    fontFamily: "'Dancing Script', cursive",
    fontSize: "20px",
    color: "#3e2723",
    marginBottom: "10px",
  },
  detailItem: {
    fontFamily: "'Dancing Script', cursive",
    fontSize: "16px",
    color: "#3e2723",
    marginBottom: "5px",
  },
  cookButton: {
    background: "#3e2723",
    color: "white",
    padding: "8px 16px",
    borderRadius: "8px",
    border: "none",
    fontFamily: "'Dancing Script', cursive",
    fontSize: "16px",
    cursor: "pointer",
    transition: "background 0.3s ease-in-out",
    marginTop: "10px",
  },
};

export default Recipes;
